mapper.insert:

    SELECT IFNULL(MAX(position), 0)+1 FROM todo where done = false

Insert into todo (title,username,done,position,created_date,updated_date)
values (#{title},#{username},false,#{position},now(),now());

mapper.update:
Update todo
set done=!done,updated_date=now()
where tno=#{tno}

mapper.delete:
delete from todo
where tno in

    #{tno}

mapper.select:
select tno,title,username,done,position,created_date,updated_date
from todo
where username = #{username}

order by position
limit #{pageDto.skip} , #{pageDto.size}

select count(tno)
from todo
where username = #{username}

mapper.update:
update todo set position=0 where tno=#{todoDto.tno};


    update todo
    set position = (position + 1)
    where position >= #{target}
    and position < #{todoDto.position}
    and done = #{todoDto.done};




    update todo
    set position = (position - 1)
    where position > #{todoDto.position}
    and position <= #{target}
    and done = #{todoDto.done};


update todo set position = #{target} where tno=#{todoDto.tno};

Update todo
set position=(SELECT IFNULL(MAX(position), 0)+1 FROM todo where done = 1- #{todoDto.done}),updated_date=now(),done = 1- #{todoDto.done}
where tno=#{todoDto.tno};

update todo
set position = (position - 1)
where position > #{todoDto.position}
and done = #{todoDto.done};

update todo
set position = (position + 1)
where position >= #{target}
and position < (SELECT IFNULL(MAX(position), 0) FROM todo where done = 1- #{todoDto.done})
and done = 1- #{todoDto.done};

update todo set position = #{target} where tno=#{todoDto.tno};
